const EmotionInfo* get_emotion_info(Emotion emotion) {
    if (emotion >= EMOTION_COUNT) {
        return NULL;
    }
    return &emotion_table[emotion];
}

Emotion get_emotion_by_name(const char* name) {
    for (int i = 0; i < EMOTION_COUNT; i++) {
        if (strcmp(emotion_table[i].name, name) == 0) {
            return (Emotion)i;
        }
    }
    return EMOJI_HAPPY;  // Default emotion (first in list)
}
